settings.custom_liquid:
{% assign active_collections = collections | sort: 'title' %}
{% assign visible_collections = active_collections | where_exp: "collection", "collection.published_at != blank" %}

<p>Total active collections: {{ visible_collections.size }}</p>

<ul>
  {% for collection in visible_collections %}
    {% assign product_count = collection.all_products_count %}

    {%- assign link_style = '' -%}
    {%- if product_count == 0 -%}
      {%- assign link_style = 'color: red;' -%}
    {%- elsif product_count < 3 -%}
      {%- assign link_style = 'color: orange;' -%}
    {%- endif -%}

    <li>
      {% if collection.url %}
        <a target="_blank" href="{{ collection.url }}" style="{{ link_style }}">{{ collection.title }}</a>
      {% else %}
        {{ collection.title }}
      {% endif %}
      ({{ product_count }} {% if product_count == 1 %}product{% else %}products{% endif %})

      <a href="https://{{ shop.permanent_domain }}/admin/collections/{{ collection.id }}"
         target="_blank"
         style="margin-left: 10px; font-size: 0.9em; color: #888;">
        [edit in admin]
      </a>
    </li>
  {% endfor %}
</ul>
