settings.custom_liquid:
{% unless product.available %}
  <script>
    window.KlaviyoOutOfStock = true;
  </script>
{% endunless %}


{% assign first_image_url = product.images[0] | img_url: 'large' %}
<script>
  window.KlaviyoProductImage = "{{ first_image_url }}";
</script>


<div id="back-in-stock-block"></div>
<script src="https://a.klaviyo.com/media/js/onsite/onsite.js"></script>
<script>
  var klaviyo = klaviyo || [];
  klaviyo.init({
    account: "XnZ6ni",
      list: "QNMQ3S",
    platform: 'shopify'
  });

const productImage = window.KlaviyoProductImage || '';

  klaviyo.enable('backinstock', {
    trigger: {
      product_page_text: 'Notify Me When Available',
      product_page_class: 'button',
      product_page_text_align: 'center',
      product_page_margin: '0px',
      alternate_anchor: 'back-in-stock-block',
      replace_anchor: false
    },
    modal: {
      headline: 'Notify me when back in stock',
      body_content: `
        <img src="${productImage}" alt="{product_name}" style="max-width:100%; height:auto; margin-bottom: 15px;" />
<p><strong>{{ product.title }}</strong></p>        
<p>Enter your email address to  receive a one-time notification if this product comes back in stock.</p>
      `,
      email_field_label: 'Email',
      button_label: 'Notify me when available',
      subscription_success_label: "You're in! We'll let you know when it's back.",
      footer_content: '',
      background_color: '#fff',
      text_color: '#252324',
      button_text_color: '#fff',
      button_background_color: '#76A90D',
      error_background_color: '#fcd6d7',
      error_text_color: '#C72E2F',
      success_background_color: '#d3efcd',
      success_text_color: '#1B9500',
      font_family: '"DM Sans", Arial, sans-serif',
      headers_font_family: '"DM Sans", Arial, sans-serif'
    }
  });
</script>


<script>
  document.addEventListener('DOMContentLoaded', function () {
    if (window.KlaviyoOutOfStock) {
      const openModal = () => {
        const klaviyoBtn = document.querySelector('#NotifyButton, .klaviyo-bis-trigger');
        if (klaviyoBtn) {
          klaviyoBtn.click();
        } else {
          setTimeout(openModal, 1000);
        }
      };
      openModal();
    }
  });
</script>

